var write=document.querySelector(".btn_write"),popup=document.querySelector(".popup_window"),overlay=document.querySelector(".overlay"),close=popup.querySelector(".popup_close"),user_name=popup.querySelector("[name=your_name]"),user_mail=popup.querySelector("[name=your_email]");write.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("popup_show"),overlay.classList.add("overlay_show"),popup.classList.contains("popup_wrong")&&popup.classList.remove("popup_wrong"), user_name.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("popup_show"),overlay.classList.remove("overlay_show")}),popup.addEventListener("submit",function(e){user_name.value&&user_mail.value||(e.preventDefault(),console.log("Заполните все поля!"), popup.classList.add("popup_wrong"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("popup_show")&&popup.classList.remove("popup_show"))});